import React, { useState } from 'react';
import './AdminLogin.css';

function AdminLogin({ onLoginSuccess }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const response = await fetch('http://localhost:5000/api/admin/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password })
      });

      const data = await response.json();

      if (data.success) {
        localStorage.setItem('adminToken', data.token);
        onLoginSuccess();
      } else {
        setError(data.message);
      }
    } catch (err) {
      setError('–°–µ—Ä–≤–µ—Ä—Ç—ç–π —Ö–æ–ª–±–æ–≥–¥–æ—Ö –∞–ª–¥–∞–∞');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="admin-login-container">
      <div className="login-box">
        <h2>üë®‚Äçüíº –ê–¥–º–∏–Ω –Ω—ç–≤—Ç—Ä—ç—Ö</h2>
        <form onSubmit={handleLogin}>
          <div className="form-group">
            <label htmlFor="username">–•—ç—Ä—ç–≥–ª—ç–≥—á–∏–π–Ω –Ω—ç—Ä</label>
            <input
              id="username"
              type="text"
              placeholder="–ù—ç–≤—Ç—Ä—ç—Ö –Ω—ç—Ä"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">–ù—É—É—Ü “Ø–≥</label>
            <input
              id="password"
              type="password"
              placeholder="–ù—É—É—Ü “Ø–≥ –æ—Ä—É—É–ª–∞—Ö"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              disabled={loading}
            />
          </div>

          {error && <div className="error-message">{error}</div>}

          <button type="submit" disabled={loading} className="login-btn">
            {loading ? '–ù—ç–≤—Ç—ç—Ä—á –±–∞–π–Ω–∞...' : '–ù—ç–≤—Ç—Ä—ç—Ö'}
          </button>
        </form>
      </div>
    </div>
  );
}

export default AdminLogin;
