import React, { useEffect, useState } from "react";
import "./app.css";
import { BrowserRouter as Router, Routes, Route, useNavigate } from "react-router-dom";
import AdminLogin from "./AdminLogin";
import AdminPanel from "./AdminPanel";
import UserCheckout from "./UserCheckout";

// Shop page component
function ShopPage({ 
  products, category, loading, cartItems, 
  setCategory, addToCart, increaseQuantity, decreaseQuantity, handleCheckout 
}) {
  return (
    <div className="app-container">
      <header className="header">
        <div className="header-content">
          <div className="logo-section">
            <div className="logo-placeholder">
              <span className="logo-text">OYU</span>
            </div>
            <div className="header-text">
              <h1 className="title">Oyu online delguur</h1>
              <p className="subtitle">üõçÔ∏è–ú–∞–Ω–∞–π –¥—ç–ª–≥“Ø“Ø—Ä –Ω—å #–æ–Ω–ª–∞–π–Ω –±”©–≥”©”©–¥ —á–∞–Ω–∞—Ä—Ç–∞–π –±–∞—Ä–∞–∞–≥ #—Ö–∞–º–≥–∏–π–Ω_—Ö—è–º–¥ “Ø–Ω—ç—ç—Ä –Ω–∞–π—Ä—Å–∞–≥ “Ø–π–ª—á–∏–ª–≥—ç—ç–≥—ç—ç—Ä —Å–∞–Ω–∞–ª –±–æ–ª–≥–æ—Ö—ã–≥ –∑–æ—Ä–∏–Ω –∞–∂–∏–ª–ª–∞–¥–∞–≥ ü§ó –ë“Ø—Ö –±–∞—Ä–∞–∞ #—Ö“Ø—Ä–≥—ç–ª—Ç—Ç—ç–π. –ë–∏–¥ —Ç–∞–Ω–¥ ”©”©—Ä—Å–¥–∏–π–Ω —Ç—É—Ä—à–∏–∂ “Ø–∑—Å—ç–Ω –±–∞—Ä–∞–∞–≥–∞–∞ —Å–∞–Ω–∞–ª –±–æ–ª–≥–æ–¥–æ–≥ –≥—ç–¥–≥—ç—ç—Ä—ç—ç –æ–Ω—Ü–ª–æ–≥—Ç–æ–π üíï</p>
            </div>
          </div>
        </div>
      </header>

      <div className="content">
        <div className="filter-section">
          <label className="filter-label">–ê–Ω–≥–∏–ª–∞–ª —Å–æ–Ω–≥–æ—Ö:</label>
          <select 
            className="filter-select" 
            onChange={e => setCategory(e.target.value)} 
            value={category}
          >
            <option value="">üè™ –ë“Ø—Ö –±–∞—Ä–∞–∞–≥ —Ö–∞—Ä–∞—Ö</option>
            <option value="baby">üë∂ –•“Ø“Ø—Ö–¥–∏–π–Ω –±–∞—Ä–∞–∞</option>
            <option value="moms">üë© –¢”©—Ä—Å”©–Ω —ç—Ö–∏–π–Ω –±–∞—Ä–∞–∞</option>
          </select>

          <div className="header-buttons">
            <button 
              onClick={handleCheckout}
              className={`checkout-btn ${cartItems.length === 0 ? 'disabled' : ''}`}
              disabled={cartItems.length === 0}
            >
              üõí –°–∞–≥—Å ({cartItems.length})
            </button>
          </div>
        </div>

        {loading && <p className="loading">–ê—á–∞–∞–ª–∂ –±–∞–π–Ω–∞...</p>}

        {!loading && products.length === 0 && (
          <p className="no-products">–≠–Ω—ç –∞–Ω–≥–∏–ª–ª–¥ –±–∞—Ä–∞–∞ –±–∞–π—Ö–≥“Ø–π –±–∞–π–Ω–∞</p>
        )}

        <div className="products-grid">
          {products.map((p) => (
            <div key={p._id || Math.random()} className="product-card">
              <div className="product-image-wrapper">
                {p.image ? (
                  <img src={p.image} alt={p.name} className="product-image" />
                ) : (
                  <div className="product-image-placeholder">üì¶</div>
                )}
              </div>
              <div className="product-info">
                <h3 className="product-name">{p.name}</h3>
                <p className="product-description">{p.description}</p>
                <div className="product-footer">
                  <span className="product-price">{p.price}‚ÇÆ</span>
                  <button 
                    onClick={() => addToCart(p)}
                    className="add-to-cart-btn"
                  >
                    –°–∞–≥—Å –¥—ç—ç—Ä –Ω—ç–º—ç—Ö
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      <footer className="footer">
        <p>¬© 2026.Oyu online delguur. –ó–æ—Ö–∏–æ–≥—á–∏–π–Ω –±“Ø—Ö —ç—Ä—Ö —Ö—É—É–ª–∏–∞—Ä —Ö–∞–º–≥–∞–∞–ª–∞–≥–¥—Å–∞–Ω –±–æ–ª–Ω–æ.</p>
      </footer>
    </div>
  );
}

function App() {
  const navigate = useNavigate();
  const [products, setProducts] = useState([]);
  const [category, setCategory] = useState("");
  const [loading, setLoading] = useState(false);
  const [isAdminLoggedIn, setIsAdminLoggedIn] = useState(false);
  const [cartItems, setCartItems] = useState([]);
  const [isCheckout, setIsCheckout] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('adminToken');
    if (token) {
      setIsAdminLoggedIn(true);
    }
  }, []);

  useEffect(() => {
    setLoading(true);
    fetch(`http://localhost:5000/api/products${category ? '?category=' + category : ''}`)
      .then(res => res.json())
      .then(data => {
        setProducts(data || []);
        setLoading(false);
      })
      .catch(err => {
        console.log("–°–µ—Ä–≤–µ—Ä —Ö–æ–ª–±–æ–≥–¥–æ–æ–≥“Ø–π –±–∞–π–Ω–∞");
        setLoading(false);
      });
  }, [category]);

  const handleAdminLogin = () => {
    setIsAdminLoggedIn(true);
    navigate('/');
  };

  const handleAdminLogout = () => {
    localStorage.removeItem('adminToken');
    setIsAdminLoggedIn(false);
    navigate('/');
  };

  const addToCart = (product) => {
    setCartItems(prev => {
      const existingItem = prev.find(item => item._id === product._id);
      if (existingItem) {
        return prev.map(item =>
          item._id === product._id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      } else {
        return [...prev, { ...product, quantity: 1 }];
      }
    });
  };

  const removeFromCart = (productId) => {
    setCartItems(prev => prev.filter(item => item._id !== productId));
  };

  const increaseQuantity = (productId) => {
    setCartItems(prev => 
      prev.map(item =>
        item._id === productId
          ? { ...item, quantity: item.quantity + 1 }
          : item
      )
    );
  };

  const decreaseQuantity = (productId) => {
    setCartItems(prev => 
      prev.map(item =>
        item._id === productId && item.quantity > 1
          ? { ...item, quantity: item.quantity - 1 }
          : item
      ).filter(item => item.quantity > 0)
    );
  };

  const handleCheckout = () => {
    if (cartItems.length === 0) {
      alert('–°–∞–≥—Å —Ö–æ–æ—Å –±–∞–π–Ω–∞');
      return;
    }
    setIsCheckout(true);
  };

  const handleOrderSuccess = () => {
    setCartItems([]);
    setIsCheckout(false);
    alert('‚úÖ –ó–∞—Ö–∏–∞–ª–≥–∞ –∞–º–∂–∏–ª—Ç—Ç–∞–π —Ö“Ø–ª—ç—ç–Ω –∞–≤–ª–∞–∞!');
  };

  // –ê–¥–º–∏–Ω –±“Ø—Ç—ç—ç–Ω –±–∞–π–≤–∞–ª –∞–¥–º–∏–Ω –ø–∞–Ω–µ–ª—å —Ö–∞—Ä—É—É–ª–∞—Ö
  if (isAdminLoggedIn) {
    return <AdminPanel onLogout={handleAdminLogout} />;
  }

  // Checkout –¥—ç–ª–≥—ç—Ü
  if (isCheckout) {
    return (
      <UserCheckout 
        cartItems={cartItems}
        onOrderSuccess={handleOrderSuccess}
        onBack={() => setIsCheckout(false)}
        onIncreaseQuantity={increaseQuantity}
        onDecreaseQuantity={decreaseQuantity}
      />
    );
  }

  // –ù—ç–≤—Ç—Ä—ç—Ö —Ñ–æ—Ä–º –±–∞–π—Ö —ç—Å—ç—Ö –ø—Ä–æ–≤–µ—Ä–ª–∞—Ö
  // –ê–¥–º–∏–Ω –≥–æ—Ä–∏–º–¥ –æ—Ä–æ—Ö —Ç–æ–≤—á
  const isShowAdminBtn = window.location.hash === '#admin-login';

  return (
    <div className="app-container">
      <header className="header">
        <div className="header-content">
          <div className="logo-section">
            <div className="logo-placeholder">
              <span className="logo-text">OYU</span>
            </div>
            <div className="header-text">
              <h1 className="title">Oyu online delguur</h1>
              <p className="subtitle">üõçÔ∏è–ú–∞–Ω–∞–π –¥—ç–ª–≥“Ø“Ø—Ä –Ω—å #–æ–Ω–ª–∞–π–Ω –±”©–≥”©”©–¥ —á–∞–Ω–∞—Ä—Ç–∞–π –±–∞—Ä–∞–∞–≥ #—Ö–∞–º–≥–∏–π–Ω_—Ö—è–º–¥ “Ø–Ω—ç—ç—Ä –Ω–∞–π—Ä—Å–∞–≥ “Ø–π–ª—á–∏–ª–≥—ç—ç–≥—ç—ç—Ä —Å–∞–Ω–∞–ª –±–æ–ª–≥–æ—Ö—ã–≥ –∑–æ—Ä–∏–Ω –∞–∂–∏–ª–ª–∞–¥–∞–≥ ü§ó –ë“Ø—Ö –±–∞—Ä–∞–∞ #—Ö“Ø—Ä–≥—ç–ª—Ç—Ç—ç–π. –ë–∏–¥ —Ç–∞–Ω–¥ ”©”©—Ä—Å–¥–∏–π–Ω —Ç—É—Ä—à–∏–∂ “Ø–∑—Å—ç–Ω –±–∞—Ä–∞–∞–≥–∞–∞ —Å–∞–Ω–∞–ª –±–æ–ª–≥–æ–¥–æ–≥ –≥—ç–¥–≥—ç—ç—Ä—ç—ç –æ–Ω—Ü–ª–æ–≥—Ç–æ–π üíï</p>
            </div>
          </div>
        </div>
      </header>

      <div className="content">
        <div className="filter-section">
          <label className="filter-label">–ê–Ω–≥–∏–ª–∞–ª —Å–æ–Ω–≥–æ—Ö:</label>
          <select 
            className="filter-select" 
            onChange={e => setCategory(e.target.value)} 
            value={category}
          >
            <option value="">üè™ –ë“Ø—Ö –±–∞—Ä–∞–∞–≥ —Ö–∞—Ä–∞—Ö</option>
            <option value="baby">üë∂ –•“Ø“Ø—Ö–¥–∏–π–Ω –±–∞—Ä–∞–∞</option>
            <option value="moms">üë© –¢”©—Ä—Å”©–Ω —ç—Ö–∏–π–Ω –±–∞—Ä–∞–∞</option>
          </select>

          <div className="header-buttons">
            <button 
              onClick={handleCheckout}
              className={`checkout-btn ${cartItems.length === 0 ? 'disabled' : ''}`}
              disabled={cartItems.length === 0}
            >
              üõí –°–∞–≥—Å ({cartItems.length})
            </button>

            {!isAdminLoggedIn && (
              <button 
                onClick={() => {
                  if (window.location.hash === '#admin-login') {
                    setIsAdminLoggedIn(true);
                    window.location.hash = '';
                  } else {
                    window.location.hash = 'admin-login';
                  }
                }} 
                className="admin-access-btn"
              >
                {window.location.hash === '#admin-login' ? 'üë®‚Äçüíº –ê–¥–º–∏–Ω' : ''}
              </button>
            )}
          </div>
        </div>

        {isShowAdminBtn && !isAdminLoggedIn && (
          <AdminLogin onLoginSuccess={handleAdminLogin} />
        )}

        {!isShowAdminBtn && (
          <>
            {loading && <p className="loading">–ê—á–∞–∞–ª–∂ –±–∞–π–Ω–∞...</p>}

            {!loading && products.length === 0 && (
              <p className="no-products">–≠–Ω—ç –∞–Ω–≥–∏–ª–ª–¥ –±–∞—Ä–∞–∞ –±–∞–π—Ö–≥“Ø–π –±–∞–π–Ω–∞</p>
            )}

            <div className="products-grid">
              {products.map((p) => (
                <div key={p._id || Math.random()} className="product-card">
                  <div className="product-image-wrapper">
                    {p.image ? (
                      <img src={p.image} alt={p.name} className="product-image" />
                    ) : (
                      <div className="product-image-placeholder">üì¶</div>
                    )}
                  </div>
                  <div className="product-info">
                    <h3 className="product-name">{p.name}</h3>
                    <p className="product-description">{p.description}</p>
                    <div className="product-footer">
                      <span className="product-price">{p.price}‚ÇÆ</span>
                      <button 
                        onClick={() => addToCart(p)}
                        className="add-to-cart-btn"
                      >
                        –°–∞–≥—Å –¥—ç—ç—Ä –Ω—ç–º—ç—Ö
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </>
        )}
      </div>

      <footer className="footer">
        <p>¬© 2026.Oyu online delguur. –ó–æ—Ö–∏–æ–≥—á–∏–π–Ω –±“Ø—Ö —ç—Ä—Ö —Ö—É—É–ª–∏–∞—Ä —Ö–∞–º–≥–∞–∞–ª–∞–≥–¥—Å–∞–Ω –±–æ–ª–Ω–æ.</p>
      </footer>
    </div>
  );
}

export default App;
