import React, { useEffect, useState } from "react";
import "./app.css";
import { BrowserRouter as Router, Routes, Route, useNavigate, Link } from "react-router-dom";
import AdminLogin from "./AdminLogin";
import AdminPanel from "./AdminPanel";
import UserCheckout from "./UserCheckout";

// Shop page component
function ShopPage({ 
  products, category, loading, cartItems, 
  setCategory, addToCart, increaseQuantity, decreaseQuantity, handleCheckout 
}) {
  const [selectedImageIndex, setSelectedImageIndex] = useState({});
  const [zoomImage, setZoomImage] = useState(null);

  const handleImageClick = (image) => {
    setZoomImage(image);
  };

  const closeZoom = () => {
    setZoomImage(null);
  };

  const selectProductImage = (productId, index) => {
    setSelectedImageIndex(prev => ({
      ...prev,
      [productId]: index
    }));
  };

  const getCurrentImage = (product) => {
    const index = selectedImageIndex[product._id];
    // –•—ç—Ä—ç–≤ thumbnail —Å–æ–Ω–≥–æ—Å–æ–Ω –±–æ–ª images –º–∞—Å—Å–∏–≤–∞–∞—Å –∞–≤–∞—Ö
    if (index !== undefined && product.images && product.images[index]) {
      return product.images[index];
    }
    // “Æ–≥“Ø–π –±–æ–ª “Ø–Ω–¥—Å—ç–Ω –∑—É—Ä–≥–∏–π–≥ —Ö–∞—Ä—É—É–ª–∞—Ö
    return product.image;
  };

  return (
    <div className="app-container">
      <header className="header">
        <div className="header-content">
          <div className="logo-section">
            <div className="logo-placeholder">
              <span className="logo-text">OYU</span>
            </div>
            <div className="header-text">
              <h1 className="title">Oyu online delguur</h1>
