import React, { useState, useEffect } from 'react';
import './OrdersView.css';

function OrdersView() {
  const [orders, setOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchOrders();
    const interval = setInterval(fetchOrders, 3000);
    return () => clearInterval(interval);
  }, []);

  const fetchOrders = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/orders');
      const data = await response.json();
      setOrders(data);
    } catch (err) {
      console.error('–ê–ª–¥–∞–∞:', err);
    }
  };

  const handleSelectOrder = async (order) => {
    setSelectedOrder(order);
  };

  const closeDetails = () => {
    setSelectedOrder(null);
  };

  const handleDeleteOrder = async (orderId) => {
    if (!window.confirm('–≠–Ω—ç –∑–∞—Ö–∏–∞–ª–≥—ã–≥ —É—Å—Ç–≥–∞—Ö —É—É?')) {
      return;
    }

    try {
      const response = await fetch(`http://localhost:5000/api/orders/${orderId}`, {
        method: 'DELETE'
      });

      if (response.ok) {
        fetchOrders();
        alert('‚úÖ –ó–∞—Ö–∏–∞–ª–≥–∞ —É—Å—Ç–≥–∞–≥–¥–ª–∞–∞');
      }
    } catch (err) {
      alert('‚ùå –ê–ª–¥–∞–∞ –≥–∞—Ä–ª–∞–∞');
    }
  };

  const handleUpdateStatus = async (orderId, status) => {
    try {
      const response = await fetch(`http://localhost:5000/api/orders/${orderId}/status`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ status })
      });

      if (response.ok) {
        fetchOrders();
      }
    } catch (err) {
      alert('‚ùå –ê–ª–¥–∞–∞ –≥–∞—Ä–ª–∞–∞');
    }
  };

  return (
    <div className="orders-view">
      <h2>üìã –•—ç—Ä—ç–≥–ª—ç–≥—á–∏–π–Ω –∑–∞—Ö–∏–∞–ª–≥—É—É–¥ ({orders.length})</h2>

      {orders.length === 0 ? (
        <div className="no-orders">
          <p>–ó–∞—Ö–∏–∞–ª–≥–∞ –±–∞–π—Ö–≥“Ø–π –±–∞–π–Ω–∞</p>
        </div>
      ) : (
        <div className="orders-table-wrapper">
          <table className="orders-table">
            <thead>
              <tr>
                <th>–•—ç—Ä—ç–≥–ª—ç–≥—á</th>
                <th>–£—Ç–∞—Å</th>
                <th>–•–∞—è–≥</th>
                <th>–î“Ø–Ω</th>
                <th>–û–≥–Ω–æ–æ</th>
                <th>–°—Ç–∞—Ç—É—Å</th>
                <th>“Æ–π–ª–¥—ç–ª</th>
              </tr>
            </thead>
            <tbody>
              {orders.map(order => (
                <tr key={order._id}>
                  <td className="customer-name">{order.customerName}</td>
                  <td>{order.phone}</td>
                  <td className="address-cell">
                    <div>{order.address}</div>
                    {order.notes && (
                      <small className="address-details">
                        üí¨ {order.notes}
                      </small>
                    )}
                  </td>
                  <td className="price">{order.totalPrice}‚ÇÆ</td>
                  <td className="date">{order.orderDate}</td>
                  <td className="status">
                    <span className={`status-badge ${order.status === '–•“Ø—Ä–≥—ç–≥–¥—Å—ç–Ω' ? 'delivered' : 'pending'}`}>
                      {order.status || '–•“Ø–ª—ç—ç–≥–¥—ç–∂ –±–∞–π–≥–∞–∞'}
                    </span>
                  </td>
                  <td className="actions">
                    <button 
                      onClick={() => handleSelectOrder(order)}
                      className="detail-btn"
                      title="–î—ç–ª–≥—ç—Ä—ç–Ω–≥“Ø–π —Ö–∞—Ä–∞—Ö"
                    >
