import React, { useEffect, useState } from "react";

function App() {
  const [products, setProducts] = useState([]);
  const [category, setCategory] = useState("");

  useEffect(() => {
    fetch(`http://localhost:5000/api/products${category ? '?category=' + category : ''}`)
      .then(res => res.json())
      .then(setProducts);
  }, [category]);

  return (
    <div style={{ maxWidth: 700, margin: "0 auto" }}>
      <h1>Ээж болон хүүхдийн онлайн дэлгүүр</h1>
      <select onChange={e => setCategory(e.target.value)} value={category}>
        <option value="">-- Бүгд --</option>
        <option value="baby">Хүүхдийн бараа</option>
        <option value="moms">Төрсөн эхийн бараа</option>
      </select>
      <div style={{ display: "flex", flexWrap: "wrap", gap: 20, marginTop: 20 }}>
        {products.map((p) => (
          <div key={p._id} style={{ border: "1px solid #ccc", padding: 12, width: 200 }}>
            <img src={p.image} alt={p.name} style={{ width: "100%", height: 120, objectFit: "cover" }} />
            <h4>{p.name}</h4>
            <p>{p.description}</p>
            <b>{p.price}₮</b>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
