        });

        if (response.ok) {
          setMessage('‚úÖ –ë–∞—Ä–∞–∞ –∞–º–∂–∏–ª—Ç—Ç–∞–π –∑–∞—Å–∞–≥–¥–ª–∞–∞');
          setEditingId(null);
          resetForm();
          fetchProducts();
        }
      } else {
        // –ù—ç–º—ç—Ö
        const response = await fetch('http://localhost:5000/api/products', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(formData)
        });

