                  onChange={handleInputChange}
                  placeholder="10"
                  required
                />
              </div>

              <div className="form-group">
                <label>–ê–Ω–≥–∏–ª–∞–ª *</label>
                <select
                  name="category"
                  value={formData.category}
                  onChange={handleInputChange}
                  required
                >
                  <option value="baby">üë∂ –•“Ø“Ø—Ö–¥–∏–π–Ω –±–∞—Ä–∞–∞</option>
                  <option value="moms">üë© –¢”©—Ä—Å”©–Ω —ç—Ö–∏–π–Ω –±–∞—Ä–∞–∞</option>
                </select>
